      openMenus.push(menuId);
      menu.style.display = "block";

      // Добавляем обработчик для закрытия меню при клике вне него
      document.addEventListener("click", closeMenuHandler);
    } else {
      // Если меню уже открыто, закрываем его
      menu.classList.remove("open");
      openMenus = openMenus.filter((openMenuId) => openMenuId !== menuId);
      menu.style.display = "none";

      // Удаляем обработчик при закрытии меню
      document.removeEventListener("click", closeMenuHandler);
    }

