let openMenus = [];

export function toggleMenu(menuId) {
  const menu = document.getElementById(menuId);

  if (!menu) return;

  const isOpen = menu.classList.contains("open");

  closeOpenMenus();

  menu.classList.toggle("open", !isOpen);
  if (!isOpen) {
    openMenus.push(menuId);
  }

  menu.addEventListener("mouseleave", () => {
    if (!isCursorOverElement(menu) && !isCursorOverLink(menuId)) {
      closeMenu(menuId);
    }
  });

  // Добавляем обработку события mouseenter для ссылок
  const links = menu.querySelectorAll(`[data-menu="${menuId}"]`);
  links.forEach((link) => {
    link.addEventListener("mouseenter", () => {
      if (!openMenus.includes(menuId)) {
        openMenus.push(menuId);
      }
    });
  });
}

function closeOpenMenus() {
  openMenus.forEach((openMenuId) => {
    const openMenu = document.getElementById(openMenuId);
    openMenu && openMenu.classList.remove("open");
  });
  openMenus = [];
}

function closeMenu(menuId) {
  const menu = document.getElementById(menuId);
  menu && menu.classList.remove("open");
  openMenus = openMenus.filter((openMenuId) => openMenuId !== menuId);
}

function isCursorOverElement(element) {
  const rect = element.getBoundingClientRect();
  return (
    rect.top <= event.clientY &&
    event.clientY <= rect.bottom &&
    rect.left <= event.clientX &&
    event.clientX <= rect.right
  );
}

function isCursorOverLink(menuId) {
  const link = document.querySelector(`[data-menu="${menuId}"]`);
  if (link) {
    const linkRect = link.getBoundingClientRect();
    return (
      linkRect.top <= event.clientY &&
      event.clientY <= linkRect.bottom &&
      linkRect.left <= event.clientX &&
      event.clientX <= linkRect.right
    );
  }
  return false;
}

document.addEventListener("mouseleave", () => {
  closeOpenMenus();
});
