let openMenus = [];

// Add a click event listener to the document
document.addEventListener("click", (event) => {
  // Check if the click is outside any open menu
  if (!isClickInsideAnyMenu(event)) {
    closeAllMenus();
  }
});

export function toggleMenu(menuId) {
  var menu = document.getElementById(menuId);

  if (menu) {
    const isOpen = menu.classList.contains("open");

    // Close other open menus
    closeAllMenus(menuId);

    menu.classList.toggle("open", !isOpen);

    if (!isOpen) {
      openMenus.push(menuId);
    }
  }
}

function closeAllMenus(excludeMenuId) {
  openMenus.forEach((openMenuId) => {
    if (openMenuId !== excludeMenuId) {
      const openMenu = document.getElementById(openMenuId);
      if (openMenu) {
        openMenu.classList.remove("open");
      }
    }
  });
  openMenus = [];
}

function isClickInsideAnyMenu(event) {
  for (const menuId of openMenus) {
    const menu = document.getElementById(menuId);
    if (menu) {
      const menuRect = menu.getBoundingClientRect();
      if (
        event.clientX >= menuRect.left &&
        event.clientX <= menuRect.right &&
        event.clientY >= menuRect.top &&
        event.clientY <= menuRect.bottom
      ) {
        return true; // Click is inside an open menu
      }
    }
  }
  return false; // Click is outside all open menus
}
