let openMenus = [];

export function closeAllMenus() {
  openMenus.forEach((openMenuId) => {
    const openMenu = document.getElementById(openMenuId);
    if (openMenu) {
      openMenu.classList.remove("open");
      setTimeout(() => {
        openMenu.style.display = "none";
      }, 50); 
    }
    else {
      setTimeout(() => {
        openMenu.style.display = "block";
      }, 50);
    }
  });

  openMenus = [];
}

export function toggleMenu(menuId) {
  closeAllMenus();

  var menu = document.getElementById(menuId);

  if (menu) {
    const isOpen = menu.classList.contains("open");

    if (!isOpen) {
      menu.style.display = "block";
      setTimeout(() => {
        menu.classList.add("open");
        openMenus.push(menuId);
      }, 50);
    } else {
      setTimeout(() => {
        menu.classList.remove("open");
        openMenus = openMenus.filter((openMenuId) => openMenuId !== menuId);
        menu.style.display = "none";
      }, 50);
    }
  }
}

// Close all menus when the script is loaded or when the page is ready
document.addEventListener("DOMContentLoaded", closeAllMenus);
